mapper.select:
select user_id,
goods_id,
quantity
from vg_user_goods
where user_id = #{userId}
and goods_id = #{goodsId}

mapper.insert:
insert into vg_user_goods (
user_id,
goods_id,
quantity
) values (
#{userId},
#{goodsId},
#{quantity}
)

mapper.update:
update vg_user_goods
set quantity = #{quantity}
where user_id = #{userId}
and goods_id = #{goodsId}
